    public static final String TestObj2HessianStr(String dataStr) throws IOException {
        Object result = JSONObject.parse("{\"name\":\"11223\"}");
        Object unwraped = unwrapType(result);

        System.out.println(unwraped);
        ByteArrayOutputStream os = new ByteArrayOutputStream();
        Hessian2Output o = new Hessian2Output(os);
        o.writeObject(unwraped);
        o.flushBuffer();
        System.out.println(os.size());
        byte[] bytes = os.toByteArray();

        FileOutputStream file = new FileOutputStream(new File("bigjson-hessian2.b64"));
        file.write(Base64.getEncoder().encode(bytes));
        file.flush();
        file.close();

        return Base64.getEncoder().encodeToString(bytes);
    }

    public static final void TestHessianStr2Dubbo(String Data) throws IOException {
        byte[] data = Base64.getDecoder().decode(Data);
        ByteArrayInputStream is = new ByteArrayInputStream(data);
        Hessian2Input in = new Hessian2Input(is);
        System.out.println("=====> Flag:" + in.readObject());
        System.out.println("=====> Resp");
        Object o = handleValue(in);
        System.out.println(o);
        System.out.println("=====> Att");
        o = handleValue(in);
        System.out.println(o);
        is.close();
    }


    private static Object handleValue(Hessian2Input in) throws IOException {
        try {
            return in.readObject();
        } catch (Exception e) {
            e.printStackTrace();
            return null;
        }
    }

    private static Object unwrapType(Object input) {
        if (input == null) {
            return null;
        }
        if (input instanceof List) {
            final List inputd = ((List) input);
            final Object[] unwrap = new Object[inputd.size()];
            for (int i = 0; i < inputd.size(); i++) {
                unwrap[i] = unwrapType(inputd.get(i));
            }
            return unwrap;
        } else if (input instanceof Map) {
            final Map inputd = (Map) input;
            Map unwrap = new HashMap(inputd.size());
            for (Object key : inputd.keySet()) {
                unwrap.put(key, unwrapType(inputd.get(key)));
            }
            return unwrap;
        } else {
            return input;
        }
    }
